    static class TextReader
    {
        public const string alfabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ ";
        static Dictionary<char, char[][]> charToAsciiArt = new Dictionary<char, char[][]>();
        public static char[][] ReadFile(string path)
        {
            string[] lines = File.ReadLines(path).ToArray();
            char[][] chars = new char[lines.Length][];
            for (int i = 0; i < lines.Length; i++)
            {
                chars[i] = lines[i].ToCharArray();


            }

            return chars;



        }

        public static void GetAlfabeth(string path)
        {
            string[] lines = File.ReadAllLines(path);

            int letters = 27;
            int height = 7;
            int width = 5;

            char[][][] alphabet = new char[letters][][];

            int lineIndex = 0;

            for (int l = 0; l < letters; l++)
            {
                alphabet[l] = new char[height][];

                for (int r = 0; r < height; r++)
                {
                    alphabet[l][r] = lines[lineIndex].PadRight(width).ToCharArray();
                    lineIndex++;
                }


                lineIndex++;
            }


            for (int i = 0; i < alfabet.Length; i++)
            {
                charToAsciiArt.Add(alfabet[i], alphabet[i]);
            }


        }







        public static char[][] GetAsciiArt(string word)
        {
            int height = 7;
            int width = 5;
            int spacing = 1;

            char[][] result = new char[height][];

            for (int r = 0; r < height; r++)
                result[r] = new char[word.Length * (width + spacing)];

            for (int i = 0; i < word.Length; i++)
            {
                char c = word[i];
                char[][] art = charToAsciiArt[c];

                for (int r = 0; r < height; r++)
                {
                    for (int col = 0; col < width; col++)
                    {
                        result[r][i * (width + spacing) + col] = art[r][col];
                    }
                }
            }

            return result;
        }



    }

