        void PlaceBombs()
        {
            string txt = txtBoxValue.Text.ToUpper();
            if (txt.Length >= 12 || txt.Length <= 0)
            {
                if (txt.Length >= 12) txtBoxValue.Text = txt.Substring(0, 11)
                ; return;
            }
            int orizontalPosition = (txt.Length - 1) % 6;
            int Line = (txt.Length - 1) / 6 * 50 * 7;
            int xToAdd = orizontalPosition * 50 * 6;



            string letterToAdd = txt[^1].ToString();
            MyName = TextReader.GetAsciiArt(letterToAdd);

            for (int i = 0; i < MyName.Length; i++)
            {
                for (int j = 0; j < MyName[i].Length; j++)
                {
                    if (MyName[i][j] == '#')
                    {
                        int r = random.Next(0, Assets.ImageAssets[0].Length);
                        bombes.Add(new Bomb(x: xToAdd + (j + 1) * 50,
                                            y: Line + ((i + 1) * 40),
                                            indexImage: r,
                                            t: themes));

                    }

                }

            }
        }
