        private void T_Tick(object? sender, EventArgs e)
        {
            Counter++;
            Proiettile[] schegge = new Proiettile[0];
            for (int i = 0; i < proiettiles.Count; i++)
            {
                proiettiles[i].Update(Counter);

                for (int j = 0; j < bombes.Count; j++)
                {
                    if (bombes[j].rect.Contains(proiettiles[i].Poistion))
                    {
                        proiettiles[i].Invalid = true;
                        schegge = bombes[j].Esplodi(Velocita, RandomRangeSelect, NumeroSchegge);
                        bombes = bombes.Where((x) => x != (bombes[j])).ToList();


                    }

                }
            }
            AddSchegge(schegge);
            CleanListProiettili();
            Invalidate();
        }
        void AddSchegge(Proiettile[] schegge)
        {

            for (int k = 0; k < schegge.Length; k++)
            {


                proiettiles.Add(schegge[k]);


            }

        }
        void CleanListProiettili()
        {

            for (int i = proiettiles.Count - 1; i >= 0; i--)
            {
                Proiettile p = proiettiles[i];

                if (p.Invalid || p.Poistion.X > this.Width || p.Poistion.X < 0 || p.Poistion.Y > this.Height || p.Poistion.Y < 0)
                {
                    proiettiles.Remove(p);

                }


            }



        }
