    class Bomb
    {
        Image tile { get { return Assets.ImageAssets[(int)tema][IndexImage]; } }
        int IndexImage { get; set; }
        Themes tema { get; set; }
        Point Location { get; set; }
        public Rectangle rect { get { return new Rectangle(Location.X, Location.Y, 50, 50); } }
        public Bomb(int x, int y, int indexImage, Themes t = Themes.Forest)
        {
            this.Location = new Point(x, y);
            this.IndexImage = indexImage;
            this.tema = t;
        }
        public void Draw(Graphics g)
        {
            g.DrawImage(tile, new Point(Location.X, Location.Y));

        }
        public Proiettile[] Esplodi(int Velocity, int RandomRange, int count)
        {

            Proiettile[] schegge = new Proiettile[count];

            double angleStep = (2 * Math.PI) / count;

            for (int i = 0; i < count; i++)
            {
                double angle = i * angleStep;

                double x = Math.Cos(angle);
                double y = Math.Sin(angle);

                schegge[i] = new Proiettile(
                    this.Location.X,
                    this.Location.Y,
                    x,
                    y,
                    Velocity,
                    RandomRange
                );
            }

            return schegge;


        }


    }
