    class Pistola
    {
        enum Direction { down, up, left, right }

        public bool ImMoving = false;
        public bool ImShooting = false;
        int Frame = 0;
        Direction direction { get; set; } = Direction.down;
        public int posx, posy;
        public Point Location { get { return new Point(posx, posy); } }
        public Rectangle Rectangle { get { return new Rectangle(Location, new Size(30, 30)); } }

        public void Draw(Graphics g, int Counter)
        {

            int frameSlower = Counter % 5;
            if (frameSlower == 0) { Frame++; }

            Frame = Frame % 4;
            if (ImMoving) { g.DrawImage(Assets.PeppeSprite[(int)direction, Frame], Location); }
            else if (ImShooting) { g.DrawImage(Assets.PeppeShooting[(int)direction, 0], Location); }
            else { g.DrawImage(Assets.PeppeSprite[0, 0], Location); }

        }
        public void Move(KeyEventArgs e)
        {
            ImMoving = true;
            switch (e.KeyCode)
            {
                case System.Windows.Forms.Keys.Up:
                    {
                        direction = Direction.up;
                        posy -= 6;
                        return;
                    }
                case System.Windows.Forms.Keys.Down:
                    {
                        direction = Direction.down;
                        posy += 6;
                        return;
                    }
                case System.Windows.Forms.Keys.Left:
                    {
                        direction = Direction.left;
                        posx -= 6;
                        return;
                    }
                case System.Windows.Forms.Keys.Right:
                    {
                        direction = Direction.right;
                        posx += 6;
                        return;
                    }

            }
        }





        public Proiettile GenerateP(MouseEventArgs e, int Velocity, int RandomRange)
        {
            double dx = e.X - Location.X;
            double dy = e.Y - Location.Y;

            double length = Math.Sqrt(dx * dx + dy * dy);


            double X = dx / length;
            double Y = dy / length;

            return new Proiettile(Location.X, Location.Y, X, Y, Velocity, RandomRange);
        }




    }


    class Proiettile
    {

        double x;
        double y;
        static int counter = 0;
        int _velocity;
        int id = 0;

        private int _inty;
        public bool Invalid { get; set; } = false;
        int RandomRange { get; set; }

        public void Update(int Counter)
        {
            CalculateVecComponentInt(ref _inty, y);
            CalculateVecComponentInt(ref _xint, x);
            if (Counter % 15 == 0) { _velocity--; }
            if (_velocity == 0) { this.Invalid = true; }


        }

        private int _xint;


        static Random _random = new Random();

        public enum Themes { Forest, City, UBahn }
        public Proiettile(int posX, int posY, double faktorX, double faktorY, int velocity = 10, int RandomRange = 0)
        {
            _xint = posX;
            _inty = posY;
            x = faktorX;
            y = faktorY;
            this._velocity = velocity;
            this.RandomRange = RandomRange;
            counter++;
            id = counter;



        }


        int CalculateVecComponentInt(ref int c, double faktor)
        {
            int a = _random.Next(-RandomRange, RandomRange);
            c += (int)Math.Round((double)_velocity * faktor) + a;
            return c;

        }
        public Point Poistion { get { return new Point(_xint, _inty); } }

        public void Draw(Graphics g, int counter)
        {

            switch ((id + counter) % 3)
            {
                case 0:
                    g.FillEllipse(Brushes.Yellow, new Rectangle(Poistion, new Size(5 + _velocity / 2, 6 + _velocity / 2)));
                    break;
                case 1:
                    g.FillEllipse(Brushes.OrangeRed, new Rectangle(Poistion, new Size(10 + _velocity / 2, 10 + _velocity / 2)));
                    break;
                case 2:
                    g.FillEllipse(Brushes.Orange, new Rectangle(Poistion, new Size(5 + _velocity / 2, 5 + _velocity / 2)));
                    break;
            }






        }

    }
